import React, { Component } from 'react';

export default class App extends Component {
  constructor() {
    super();

    this.state = {
      allCountries: [],
      filteredCountries: [],
    };
  }

  async componentDidMount() {
    const countries = await fetch('https://restcountries.eu/rest/v2/all');
    const json = await countries.json();

    const mapedCountries = json.map((country) => {
      return {
        id: country.numericCode,
        name: country.translations.br,
        flag: country.flag,
        population: country.population,
        capital: country.capital,
        region: country.region,
        language: country.languages.iso639_1,
        callingCodes: country.callingCodes,
        alpha: country.alpha,
      };
    });

    console.log(mapedCountries);

    this.setState = {
      allCountries: json,
    };
  }

  render() {
    const { allCountries } = this.state;
    return <div>{allCountries}</div>;
  }
}
