import React, { Component } from 'react';

export default class App extends Component {
  constructor() {
    super();

    this.state = {
      allCountries: [],
      filteredCountries: [],
    };
  }

  async componentDidMount() {
    const countries = await fetch('https://restcountries.eu/rest/v2/all');
    const json = await countries.json();

    const mapedCountries = json.map(
      ({
        numericCode,
        translations,
        flag,
        population,
        capital,
        region,
        languages,
        callingCodes,
        alpha3Code,
      }) => {
        return {
          id: numericCode,
          name: translations.br,
          flag,
          population,
          capital,
          region,
          language: languages[0].iso639_1,
          callingCodes,
          alpha3Code,
        };
      }
    );

    console.log(mapedCountries);

    this.setState = {
      allCountries: json,
    };
  }

  render() {
    const { allCountries } = this.state;
    return <div>{allCountries}</div>;
  }
}
