import React, { Component } from 'react';

export default class Header extends Component {
  handleInput = (event) => (filter = event.target.value);
  render() {
    const { filter } = this.props;
    return (
      <div>
        <input type="text" filter={filter} onChange={this.handleInput} />
        <span>| Países</span>
        <span>| População</span>
      </div>
    );
  }
}
