function handleContinents(continents) {
  let africa = 0;
  let americas = 0;
  let asia = 0;
  let europe = 0;
  let oceania = 0;
  let polar = 0;
  continents.find((cont) => {
    switch (cont) {
      case Africa:
        africa = +1;
        console.log(africa);
        break;
      case Americas:
        americas = +1;
        console.log(americas);
        break;
      case Asia:
        asia = +1;
        console.log(asia);
        break;
      case Europe:
        europe = +1;
        console.log(europe);
        break;
      case Oceania:
        oceania = +1;
        console.log(oceania);
        break;
      default:
        polar = +1;
        console.log(polar);
        break;
    }
  });

  return [africa, americas, asia, europe, oceania, polar];
}

export { handleContinents };
